// Primeira coisa que deve ser feita é a importacao do React
import React, { Component } from "react";

class Forms extends Component{ //A classe Forms esta extendendo atributos do React

    //Criando contrutor
    state = {

        name: '',
        sobrenome: ''
    } 
    
    handleSumit = (b) =>{

        b.preventDefault()

        let {name} = this.state
        name = name
        console.log('Nome:', name)

        let {sobrenome} = this.state
        sobrenome = sobrenome

        console.log('Sobrenome:', sobrenome)
    }

    // handleInputChange = (n) =>{

    //     this.setState({
    //         name: n.target.value
    //     })
    // }

    handleInputChange(property) {

        return x => {
          this.setState({
            [property]: x.target.value
          });
        };
      }

    render(){

        return(
            <section>
                <form onSubmit={this.handleSumit}>
                    <label>
                        nome:
                        <input onChange={this.handleInputChange('name')} type='text' placeholder='Nome'/>
                    </label>
                    <label>
                        sobrenome:
                        <input onChange={this.handleInputChange} type='text' placeholder='Sobrenome'/>
                    </label>
                    <button type = 'submit'>Enviar</button>
                </form>
            </section>
        )
    }
}

export default Forms
