// Primeira coisa que deve ser feita é a importacao do React
import React, { Component } from "react";

class Forms extends Component{ //A classe Forms esta extendendo atributos do React

    //Criando contrutor
    constructor(props){

        super(props)

        this.state = {

            name: '',
            sobrenome: ''
        }
    }

    handleInputChange(x){

        this.setState({
            nome: x.target.value
        })
    }

    render(){

        return(
            <section>
                <form>
                    <label>
                        nome:
                        <input onChange={this.handleInputChange} type='text' placeholder='Nome'/>
                    </label>
                    {/* <label>
                        sobrenome:
                        <input type='text' placeholder='Sobrenome'/>
                    </label> */}
                    <button type = 'submit'>Enviar</button>
                </form>
            </section>
        )
    }
}

export default Forms
