// Primeira coisa que deve ser feita é a importacao do React
import React, { Component } from "react";

class Forms extends Component{ //A classe Forms esta extendendo atributos do React

    //Criando contrutor
    state = {

        name: '',
    } 
    
    handleSumit = (b) =>{

        b.preventDefault()

        let {name} = this.state
        name = name
        console.log('Nome:', name)

    }

    handleInputChange = (n) =>{

        this.setState({
            name: n.target.value
        })
    }

    render(){

        return(
            <section>
                <form onSubmit={this.handleSumit}>
                    <label>
                        Nome Complto:
                        <input onChange={this.handleInputChange} type='text' placeholder='Nome'/>
                    </label>
                    <button type = 'submit'>Enviar</button>
                </form>
            </section>
        )
    }
}

export default Forms
